import { LuLoaderCircle } from "react-icons/lu";
import { Slot } from "@radix-ui/react-slot";
import { css } from "styled-system/css";
import { styled } from "styled-system/jsx";
import { button } from "styled-system/recipes";

interface ButtonProps extends React.ComponentProps<"button"> {
  loading?: boolean;
  asChild?: boolean;
}

function Root({ children, asChild, loading, disabled, ...props }: ButtonProps) {
  const Component = asChild ? Slot : "button";

  return (
    <Component disabled={loading || disabled} {...props}>
      {loading ? (
        <>
          <span className={css({ display: "contents", visibility: "hidden" })} aria-hidden>
            {children}
          </span>
          <div className={css({ srOnly: true })}>{children}</div>
          <span
            className={css({
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              pos: "absolute",
              inset: 0,
            })}
          >
            <LuLoaderCircle className={css({ animation: "spin" })} />
          </span>
        </>
      ) : (
        children
      )}
    </Component>
  );
}
const Button = styled(Root, button);
Button.displayName = "Button";

export { Button };

import { defineRecipe } from "@pandacss/dev";

export const buttonRecipe = defineRecipe({
  className: "button",
  base: {
    pos: "relative",
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center",
    gap: "2",
    flexShrink: "0",
    whiteSpace: "nowrap",
    borderWidth: "1px",
    borderColor: "transparent",
    rounded: "md",
    textStyle: "sm",
    fontWeight: "medium",
    cursor: "pointer",
    transition: "all",
    outlineColor: "transparent",
    _focusVisible: {
      borderColor: "ring",
      outlineWidth: "3px",
      outlineStyle: "solid",
      outlineColor: "ring/50",
    },
    _disabled: {
      pointerEvents: "none",
      opacity: "0.5",
    },
    _invalid: {
      borderColor: "danger",
      outlineColor: "danger/20",
    },
    "& svg": {
      pointerEvents: "none",
      w: "4",
      h: "4",
      flexShrink: "0",
    },
    _dark: {
      _invalid: {
        outlineColor: "danger/40",
      },
    },
  },
  variants: {
    variant: {
      primary: {
        bg: "primary",
        color: "primary.fg",
        shadow: "xs",
        _hover: {
          bg: "primary/90",
        },
      },
      secondary: {
        bg: "secondary",
        color: "secondary.fg",
        shadow: "xs",
        _hover: {
          bg: "secondary/80",
        },
      },
      danger: {
        bg: "danger",
        color: "white",
        shadow: "xs",
        _hover: {
          bg: "danger/90",
        },
        _focusVisible: {
          outlineColor: "danger/20",
        },
        _dark: {
          bg: "danger/60",
          _focusVisible: {
            outlineColor: "danger/40",
          },
        },
      },
      outline: {
        bg: "bg",
        borderWidth: "1px",
        borderColor: "border",
        shadow: "xs",
        _hover: {
          bg: "accent",
          color: "accent.fg",
        },
        _dark: {
          bg: "input/30",
          borderColor: "input",
          _hover: {
            bg: "input/50",
          },
        },
      },
      ghost: {
        _hover: {
          bg: "accent",
          color: "accent.fg",
        },
        _dark: {
          _hover: {
            bg: "accent/50",
          },
        },
      },
      link: {
        color: "primary",
        textUnderlineOffset: "4px",
        _hover: {
          textDecoration: "underline",
        },
      },
      input: {
        bg: "bg",
        borderWidth: "1px",
        borderColor: "input",
        shadow: "xs",
        fontWeight: "normal",
        textStyle: "md",
        md: {
          textStyle: "sm",
        },
        _hover: {
          bg: "accent",
          color: "accent.fg",
        },
        _invalid: {
          borderColor: "danger",
          outlineColor: "danger/20",
        },
        _focusVisible: {
          borderColor: "ring",
        },
        _dark: {
          bg: "input/30",
          borderColor: "input",
          _hover: {
            bg: "input/50",
          },
        },
        "&:has(> svg)": {
          px: "3",
        },
      },
    },
    size: {
      sm: {
        h: "8",
        gap: "1.5",
        px: "3",
        "&:has(> svg)": {
          px: "2.5",
        },
      },
      md: {
        h: "9",
        px: "4",
        py: "2",
        "&:has(> svg)": {
          px: "3",
        },
      },
      lg: {
        h: "10",
        px: "6",
        "&:has(> svg)": {
          px: "4",
        },
      },
      icon: {
        w: "9",
        h: "9",
      },
      "icon-sm": {
        w: "8",
        h: "8",
      },
      "icon-lg": {
        w: "10",
        h: "10",
      },
    },
  },
  defaultVariants: {
    variant: "primary",
    size: "md",
  },
});
