"use client";

import { createStyleContext } from "styled-system/jsx";
import { card } from "styled-system/recipes";

const { withProvider, withContext } = createStyleContext(card);

const Card = withProvider("div", "root");
const CardHeader = withContext("div", "header");
const CardTitle = withContext("div", "title");
const CardDescription = withContext("div", "description");
const CardAction = withContext("div", "action");
const CardContent = withContext("div", "content");
const CardFooter = withContext("div", "footer");

export { Card, CardHeader, CardTitle, CardDescription, CardAction, CardContent, CardFooter };

import { defineSlotRecipe } from "@pandacss/dev";

export const cardSlotRecipe = defineSlotRecipe({
  className: "card",
  slots: ["root", "header", "title", "description", "action", "content", "footer"],
  base: {
    root: {
      bg: "card",
      color: "card.fg",
      display: "flex",
      flexDir: "column",
      gap: "6",
      py: "6",
      rounded: "xl",
      borderWidth: "1px",
      shadow: "sm",
    },

    header: {
      containerType: "inline-size",
      containerName: "card-header",
      display: "grid",
      gridAutoRows: "min",
      gridTemplateRows: "auto auto",
      alignItems: "flex-start",
      gap: "1.5",
      px: "6",
      "&:has(.card__action)": {
        gridTemplateColumns: "1fr auto",
      },
    },

    title: {
      fontWeight: "semibold",
      lineHeight: "none",
    },

    description: {
      color: "muted.fg",
      textStyle: "sm",
    },

    action: {
      gridColumnStart: "2",
      gridRow: "span 2 / span 2",
      gridRowStart: "1",
      alignSelf: "flex-start",
      justifySelf: "end",
    },

    content: {
      px: "6",
    },

    footer: {
      display: "flex",
      alignItems: "center",
      px: "6",
    },
  },
});
