"use client";

import * as React from "react";
import { LuCheck } from "react-icons/lu";
import * as CheckboxPrimitive from "@radix-ui/react-checkbox";
import { css, cx } from "styled-system/css";
import { styled } from "styled-system/jsx";
import { checkbox } from "styled-system/recipes";

function Root({ className, ...props }: React.ComponentProps<typeof CheckboxPrimitive.Root>) {
  const classes = checkbox();

  return (
    <CheckboxPrimitive.Root className={cx("peer", classes.root, className)} {...props}>
      <CheckboxPrimitive.Indicator className={classes.indicator}>
        <LuCheck className={css({ w: "3.5", h: "3.5" })} />
      </CheckboxPrimitive.Indicator>
    </CheckboxPrimitive.Root>
  );
}
const Checkbox = styled(Root);
Checkbox.displayName = CheckboxPrimitive.Root.displayName;

export { Checkbox };

import { defineSlotRecipe } from "@pandacss/dev";

export const checkboxSlotRecipe = defineSlotRecipe({
  className: "checkbox",
  slots: ["root", "indicator"],
  base: {
    root: {
      w: "4",
      h: "4",
      flexShrink: "0",
      rounded: "sm",
      borderWidth: "1px",
      borderColor: "input",
      transitionProperty: "outline",
      transitionDuration: "fastest",
      outline: "none",
      _focusVisible: {
        borderColor: "ring",
        outlineWidth: "3px",
        outlineStyle: "solid",
        outlineColor: "ring/50",
      },
      _disabled: {
        cursor: "not-allowed",
        opacity: "0.5",
      },
      _checked: {
        bg: "primary",
        color: "primary.fg",
        borderColor: "primary",
      },
      "&[aria-invalid='true']": {
        borderColor: "danger",
        outlineColor: "danger/20",
      },
      _dark: {
        bg: "input/30",
        _checked: {
          bg: "primary",
          borderColor: "primary",
        },
        "&[aria-invalid='true']": {
          outlineColor: "danger/40",
        },
      },
    },

    indicator: {
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      color: "currentColor",
    },
  },
});
