"use client";

import * as React from "react";
import { css } from "styled-system/css";
import { createStyleContext } from "styled-system/jsx";
import { drawer } from "styled-system/recipes";
import { Drawer as DrawerPrimitive } from "vaul";

const { withRootProvider, withContext } = createStyleContext(drawer);

const Drawer = withRootProvider(DrawerPrimitive.Root);
const DrawerTrigger = withContext(DrawerPrimitive.Trigger, "trigger");
const DrawerPortal = DrawerPrimitive.Portal;
const DrawerClose = withContext(DrawerPrimitive.Close, "close");
const DrawerOverlay = withContext(DrawerPrimitive.Overlay, "overlay");

function Content({ children, ...props }: React.ComponentProps<typeof DrawerPrimitive.Content>) {
  return (
    <DrawerPortal>
      <DrawerOverlay />
      <DrawerPrimitive.Content {...props}>
        <div
          className={css({
            bg: "muted",
            mx: "auto",
            mt: "4",
            display: "none",
            w: "100px",
            h: "2",
            flexShrink: "0",
            rounded: "full",
            "[data-vaul-drawer-direction=bottom] &": {
              display: "block",
            },
          })}
        />
        {children}
      </DrawerPrimitive.Content>
    </DrawerPortal>
  );
}
const DrawerContent = withContext(Content, "content");

const DrawerHeader = withContext("div", "header");
const DrawerTitle = withContext(DrawerPrimitive.Title, "title");
const DrawerDescription = withContext(DrawerPrimitive.Description, "description");
const DrawerFooter = withContext("div", "footer");

export {
  Drawer,
  DrawerClose,
  DrawerContent,
  DrawerDescription,
  DrawerFooter,
  DrawerHeader,
  DrawerOverlay,
  DrawerPortal,
  DrawerTitle,
  DrawerTrigger,
};

import { defineSlotRecipe } from "@pandacss/dev";

export const drawerSlotRecipe = defineSlotRecipe({
  className: "drawer",
  slots: ["trigger", "overlay", "content", "header", "title", "description", "footer", "close"],
  base: {
    overlay: {
      pos: "fixed",
      inset: "0",
      zIndex: "50",
      bg: "black/50",
      _open: {
        animateIn: true,
        fadeIn: "0",
      },
      _closed: {
        animateOut: true,
        fadeOut: "0",
      },
    },

    content: {
      pos: "fixed",
      zIndex: "50",
      bg: "bg",
      display: "flex",
      h: "auto",
      flexDir: "column",
      "&[data-vaul-drawer-direction=top]": {
        insetX: "0",
        top: "0",
        mb: "24",
        maxH: "80vh",
        roundedBottom: "lg",
        borderBottomWidth: "1px",
      },
      "&[data-vaul-drawer-direction=bottom]": {
        insetX: "0",
        bottom: "0",
        mt: "24",
        maxH: "80vh",
        roundedTop: "lg",
        borderTopWidth: "1px",
      },
      "&[data-vaul-drawer-direction=right]": {
        insetY: "0",
        right: "0",
        w: "3/4",
        borderLeftWidth: "1px",
        sm: {
          maxW: "sm",
        },
      },
      "&[data-vaul-drawer-direction=left]": {
        insetY: "0",
        left: "0",
        w: "3/4",
        borderRightWidth: "1px",
        sm: {
          maxW: "sm",
        },
      },
    },

    header: {
      display: "flex",
      flexDir: "column",
      gap: "0.5",
      p: "4",
      "[data-vaul-drawer-direction=top] &": {
        textAlign: "center",
      },
      "[data-vaul-drawer-direction=bottom] &": {
        textAlign: "center",
      },
      md: {
        gap: "1.5",
        textAlign: "left",
      },
    },

    title: {
      color: "fg",
      fontWeight: "semibold",
    },

    description: {
      color: "muted.fg",
      textStyle: "sm",
    },

    footer: {
      mt: "auto",
      display: "flex",
      flexDir: "column",
      gap: "2",
      p: "4",
    },
  },
});
