"use client";

import { cva, cx, type RecipeVariantProps } from "styled-system/css";
import { createStyleContext } from "styled-system/jsx";
import { inputGroup } from "styled-system/recipes";
import { Button } from "@/registry/default/ui/button";
import { Textarea } from "@/registry/default/ui/textarea";
import { Input } from "./input";

const { withProvider, withContext } = createStyleContext(inputGroup);

const InputGroup = withProvider("div", "root", {
  defaultProps: {
    role: "group",
  },
});

function Addon({
  align = "inline-start",
  ...props
}: React.ComponentPropsWithoutRef<"div"> & {
  align?: "inline-start" | "inline-end" | "block-start" | "block-end";
}) {
  return (
    <div
      role="group"
      data-align={align}
      onClick={(e) => {
        if ((e.target as HTMLElement).closest("button")) {
          return;
        }
        e.currentTarget.parentElement?.querySelector("input")?.focus();
      }}
      {...props}
    />
  );
}

const InputGroupAddon = withContext(Addon, "addon");

const inputGroupButtonVariants = cva({
  base: {
    display: "flex",
    alignItems: "center",
    gap: "2",
    textStyle: "sm",
    shadow: "none",
  },
  variants: {
    size: {
      xs: {
        h: "6",
        gap: "1",
        px: "2",
        "& > svg": {
          w: "3.5",
          h: "3.5",
        },
        "&:has(> svg)": {
          px: "2",
        },
      },
      sm: {
        h: "8",
        px: "2.5",
        gap: "1.5",
        rounded: "md",
        "&:has(> svg)": {
          px: "2.5",
        },
      },
      "icon-xs": {
        w: "6",
        h: "6",
        p: "0",
        "&:has(> svg)": {
          p: "0",
        },
      },
      "icon-sm": {
        w: "8",
        h: "8",
        p: "0",
        "&:has(> svg)": {
          p: "0",
        },
      },
    },
  },
  defaultVariants: {
    size: "xs",
  },
});

function InputGroupButton({
  className,
  type = "button",
  variant = "ghost",
  size = "xs",
  ...props
}: Omit<React.ComponentProps<typeof Button>, "size"> &
  RecipeVariantProps<typeof inputGroupButtonVariants>) {
  return (
    <Button
      type={type}
      variant={variant}
      className={cx(inputGroupButtonVariants({ size }), className)}
      {...props}
    />
  );
}

const InputGroupText = withContext("span", "text");

function InputGroupInput({ css, ...props }: React.ComponentProps<typeof Input>) {
  return (
    <Input
      data-slot="input-group-control"
      css={{
        flex: "1",
        rounded: "0",
        borderWidth: "0",
        bg: "transparent",
        shadow: "none",
        _focusVisible: {
          outlineWidth: "0",
        },
        _dark: {
          bg: "transparent",
        },
        ...css,
      }}
      {...props}
    />
  );
}

function InputGroupTextarea({ css, ...props }: React.ComponentProps<typeof Textarea>) {
  return (
    <Textarea
      data-slot="input-group-control"
      css={{
        flex: "1",
        resize: "none",
        rounded: "none",
        borderWidth: "0",
        bg: "transparent",
        py: "3",
        shadow: "none",
        _focusVisible: {
          outlineWidth: "0",
        },
        _dark: {
          bg: "transparent",
        },
        ...css,
      }}
      {...props}
    />
  );
}

export {
  InputGroup,
  InputGroupAddon,
  InputGroupButton,
  InputGroupText,
  InputGroupInput,
  InputGroupTextarea,
};

import { defineSlotRecipe } from "@pandacss/dev";

export const inputGroupSlotRecipes = defineSlotRecipe({
  className: "input-group",
  slots: ["root", "addon", "text"],
  base: {
    root: {
      pos: "relative",
      display: "flex",
      alignItems: "center",
      w: "full",
      minW: "0",
      h: "9",
      borderWidth: "1px",
      borderColor: "input",
      rounded: "md",
      shadow: "xs",
      outlineColor: "transparent",
      transition: "common",
      _dark: {
        bg: "input/30",
      },
      "&:has(> textarea)": {
        h: "auto",
      },
      "&:has(> [data-align=inline-start]) > input": {
        pl: "2",
      },
      "&:has(> [data-align=inline-end]) > input": {
        pr: "2",
      },
      "&:has(> [data-align=block-start])": {
        h: "auto",
        flexDir: "column",
        "& > input": {
          pb: "3",
        },
      },
      "&:has(> [data-align=block-end])": {
        h: "auto",
        flexDir: "column",
        "& > input": {
          pt: "3",
        },
      },
      "&:has([data-slot=input-group-control]:focus-visible)": {
        borderColor: "ring",
        outlineWidth: "3px",
        outlineStyle: "solid",
        outlineColor: "ring/50",
      },
      "&:has([data-slot][aria-invalid=true])": {
        borderColor: "danger",
        outlineColor: "danger/20",
        _dark: {
          outlineColor: "danger/40",
        },
      },
    },

    addon: {
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      gap: "2",
      h: "auto",
      py: "1.5",
      color: "muted.fg",
      textStyle: "sm",
      fontWeight: "medium",
      cursor: "text",
      userSelect: "none",
      "& > svg": {
        w: "4",
        h: "4",
      },
      ".input-group__root[data-disabled=true] &": {
        opacity: "0.5",
      },
      "&[data-align=inline-start]": {
        order: "-9999",
        pl: "3",
        "&:has(> button)": {
          ml: "-0.45rem",
        },
        "&:has(> kbd)": {
          ml: "-0.35rem",
        },
      },
      "&[data-align=inline-end]": {
        order: "9999",
        pr: "3",
        "&:has(> button)": {
          mr: "-0.45rem",
        },
        "&:has(> kbd)": {
          mr: "-0.35rem",
        },
      },
      "&[data-align=block-start]": {
        order: "-9999",
        w: "full",
        justifyContent: "flex-start",
        p: "3",
        ".input-group__root:has(> input)": {
          pt: "2.5",
        },
      },
      "&[data-align=block-end]": {
        order: "9999",
        w: "full",
        justifyContent: "flex-start",
        p: "3",
        ".input-group__root:has(> input)": {
          pb: "2.5",
        },
      },
    },

    text: {
      display: "flex",
      alignItems: "center",
      gap: "2",
      color: "muted.fg",
      textStyle: "sm",
      "& svg": {
        pointerEvents: "none",
        w: "4",
        h: "4",
      },
    },
  },
});
