import * as React from "react";
import { Slot } from "@radix-ui/react-slot";
import { cx } from "styled-system/css";
import { styled } from "styled-system/jsx";
import { item } from "styled-system/recipes";
import { Separator } from "@/registry/default/ui/separator";

const classes = item();

function ItemGroup({ className, ...props }: React.ComponentProps<typeof styled.div>) {
  return (
    <styled.div
      role="list"
      data-slot="item-group"
      className={cx(classes.group, className)}
      {...props}
    />
  );
}

function ItemSeparator({ css, ...props }: React.ComponentProps<typeof Separator>) {
  return (
    <Separator
      data-slot="item-separator"
      orientation="horizontal"
      css={{ my: "0", ...css }}
      {...props}
    />
  );
}

function Item({
  className,
  variant = "default",
  size = "default",
  asChild = false,
  ...props
}: React.ComponentProps<typeof styled.div> & {
  variant?: "default" | "outline" | "muted";
  size?: "default" | "sm";
  asChild?: boolean;
}) {
  const Comp = asChild ? Slot : styled.div;
  return (
    <Comp
      data-slot="item"
      data-variant={variant}
      data-size={size}
      className={cx(classes.root, className)}
      {...props}
    />
  );
}

function ItemMedia({
  className,
  variant = "default",
  ...props
}: React.ComponentProps<typeof styled.div> & { variant?: "default" | "icon" | "image" }) {
  return (
    <styled.div
      data-slot="item-media"
      data-variant={variant}
      className={cx(classes.media, className)}
      {...props}
    />
  );
}

function ItemContent({ className, ...props }: React.ComponentProps<typeof styled.div>) {
  return (
    <styled.div data-slot="item-content" className={cx(classes.content, className)} {...props} />
  );
}

function ItemTitle({ className, ...props }: React.ComponentProps<typeof styled.div>) {
  return <styled.div data-slot="item-title" className={cx(classes.title, className)} {...props} />;
}

function ItemDescription({ className, ...props }: React.ComponentProps<typeof styled.p>) {
  return (
    <styled.p
      data-slot="item-description"
      className={cx(classes.description, className)}
      {...props}
    />
  );
}

function ItemActions({ className, ...props }: React.ComponentProps<typeof styled.div>) {
  return (
    <styled.div data-slot="item-actions" className={cx(classes.actions, className)} {...props} />
  );
}

function ItemHeader({ className, ...props }: React.ComponentProps<typeof styled.div>) {
  return (
    <styled.div data-slot="item-header" className={cx(classes.header, className)} {...props} />
  );
}

function ItemFooter({ className, ...props }: React.ComponentProps<typeof styled.div>) {
  return (
    <styled.div data-slot="item-footer" className={cx(classes.footer, className)} {...props} />
  );
}

export {
  Item,
  ItemMedia,
  ItemContent,
  ItemActions,
  ItemGroup,
  ItemSeparator,
  ItemTitle,
  ItemDescription,
  ItemHeader,
  ItemFooter,
};

import { defineSlotRecipe } from "@pandacss/dev";

export const itemSlotRecipes = defineSlotRecipe({
  className: "item",
  slots: [
    "group",
    "separator",
    "root",
    "media",
    "content",
    "title",
    "description",
    "actions",
    "header",
    "footer",
  ],
  base: {
    group: {
      display: "flex",
      flexDirection: "column",
    },

    separator: {},

    root: {
      display: "flex",
      alignItems: "center",
      flexWrap: "wrap",
      borderWidth: "1px",
      borderColor: "transparent",
      textStyle: "sm",
      rounded: "md",
      transition: "colors",
      outlineColor: "transparent",
      _focusVisible: {
        borderColor: "ring",
        outlineWidth: "3px",
        outlineStyle: "solid",
        outlineColor: "ring/50",
      },
      "&:is(a)": {
        transition: "colors",
        transitionDuration: "fast",
        _hover: {
          bg: "accent/50",
        },
      },
      "&[data-variant=default]": {
        bg: "transparent",
      },
      "&[data-variant=outline]": {
        borderColor: "border",
      },
      "&[data-variant=muted]": {
        bg: "muted/50",
      },
      "&[data-size=default]": {
        p: "4",
        gap: "4",
      },
      "&[data-size=sm]": {
        px: "4",
        py: "3",
        gap: "2.5",
      },
    },

    media: {
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      gap: "2",
      flexShrink: "0",
      ".item__root:has(.item__description) &": {
        alignSelf: "flex-start",
        transform: "translateY(0.125rem)",
      },
      "& svg": {
        pointerEvents: "none",
      },
      "&[data-variant=default]": {
        bg: "transparent",
      },
      "&[data-variant=icon]": {
        bg: "muted",
        w: "8",
        h: "8",
        borderWidth: "1px",
        rounded: "sm",
        "& svg": {
          w: "4",
          h: "4",
        },
      },
      "&[data-variant=image]": {
        w: "10",
        h: "10",
        rounded: "sm",
        overflow: "hidden",
        "& img": {
          w: "full",
          h: "full",
          objectFit: "cover",
        },
      },
    },

    content: {
      display: "flex",
      flex: "1",
      flexDir: "column",
      gap: "1",
      "& + .item__content": {
        flex: "none",
      },
    },

    title: {
      display: "flex",
      alignItems: "center",
      gap: "2",
      w: "fit",
      textStyle: "sm",
      lineHeight: "snug",
      fontWeight: "medium",
    },

    description: {
      color: "muted.fg",
      lineClamp: "2",
      textStyle: "sm",
      lineHeight: "normal",
      fontWeight: "normal",
      textWrap: "balance",
      "& > a": {
        textDecoration: "underline",
        textUnderlineOffset: "4px",
        _hover: {
          color: "primary",
        },
      },
    },

    actions: {
      display: "flex",
      alignItems: "center",
      gap: "2",
    },

    header: {
      display: "flex",
      flexBasis: "full",
      alignItems: "center",
      justifyContent: "space-between",
      gap: "2",
    },

    footer: {
      display: "flex",
      flexBasis: "full",
      alignItems: "center",
      justifyContent: "space-between",
      gap: "2",
    },
  },
});
