import { styled } from "styled-system/jsx";
import { kbd, kbdGroup } from "styled-system/recipes";

const Kbd = styled("kbd", kbd);
const KbdGroup = styled("kbd", kbdGroup);

export { Kbd, KbdGroup };

import { defineRecipe } from "@pandacss/dev";

export const kbdRecipe = defineRecipe({
  className: "kbd",
  base: {
    bg: "muted",
    color: "muted.fg",
    pointerEvents: "none",
    display: "inline-flex",
    w: "fit",
    minW: "5",
    h: "5",
    alignItems: "center",
    justifyContent: "center",
    gap: "1",
    rounded: "sm",
    px: "1",
    fontFamily: "var(--global-font-body)",
    textStyle: "xs",
    fontWeight: "medium",
    userSelect: "none",
    "& svg": {
      w: "3",
      h: "3",
    },
    ".tooltip__content &": {
      bg: "bg/20",
      color: "bg",
      _dark: {
        bg: "bg/10",
      },
    },
  },
});

import { defineRecipe } from "@pandacss/dev";

export const kbdGroupRecipe = defineRecipe({
  className: "kbd-group",
  base: {
    display: "inline-flex",
    alignItems: "center",
    gap: "1",
  },
});
