"use client";

import * as React from "react";
import { LuCheck, LuChevronRight, LuCircle } from "react-icons/lu";
import * as MenubarPrimitive from "@radix-ui/react-menubar";
import { css } from "styled-system/css";
import { createStyleContext } from "styled-system/jsx";
import { menubar } from "styled-system/recipes";

const { withProvider, withContext } = createStyleContext(menubar);

const Menubar = withProvider(MenubarPrimitive.Root, "root", {
  defaultProps: {
    "data-slot": "menubar",
  },
});

const MenubarMenu = withContext(MenubarPrimitive.Menu, "menu", {
  defaultProps: {
    "data-slot": "menubar-menu",
  },
});

const MenubarGroup = withContext(MenubarPrimitive.Group, "group", {
  defaultProps: {
    "data-slot": "menubar-group",
  },
});

const MenubarPortal = MenubarPrimitive.Portal;

const MenubarRadioGroup = withContext(MenubarPrimitive.RadioGroup, "radioGroup", {
  defaultProps: {
    "data-slot": "menubar-radio-group",
  },
});

const MenubarTrigger = withContext(MenubarPrimitive.Trigger, "trigger", {
  defaultProps: {
    "data-slot": "menubar-trigger",
  },
});

function Content({
  align = "start",
  alignOffset = -4,
  sideOffset = 8,
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.Content>) {
  return (
    <MenubarPortal>
      <MenubarPrimitive.Content
        data-slot="menubar-content"
        align={align}
        alignOffset={alignOffset}
        sideOffset={sideOffset}
        {...props}
      />
    </MenubarPortal>
  );
}
const MenubarContent = withContext(Content, "content");

function Item({
  inset,
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.Item> & { inset?: boolean }) {
  return <MenubarPrimitive.Item data-slot="menubar-item" data-inset={inset} {...props} />;
}
const MenubarItem = withContext(Item, "item");

function CheckboxItem({
  children,
  checked,
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.CheckboxItem>) {
  return (
    <MenubarPrimitive.CheckboxItem data-slot="menubar-checkbox-item" checked={checked} {...props}>
      <span
        className={css({
          pos: "absolute",
          left: "2",
          display: "flex",
          w: "3.5",
          h: "3.5",
          alignItems: "center",
          justifyContent: "center",
          pointerEvents: "none",
        })}
      >
        <MenubarPrimitive.ItemIndicator>
          <LuCheck className={css({ w: "4", h: "4" })} />
        </MenubarPrimitive.ItemIndicator>
      </span>
      {children}
    </MenubarPrimitive.CheckboxItem>
  );
}
const MenubarCheckboxItem = withContext(CheckboxItem, "checkboxItem");

function RadioItem({
  children,
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.RadioItem>) {
  return (
    <MenubarPrimitive.RadioItem data-slot="menubar-radio-item" {...props}>
      <span
        className={css({
          pos: "absolute",
          left: "2",
          display: "flex",
          w: "3.5",
          h: "3.5",
          alignItems: "center",
          justifyContent: "center",
          pointerEvents: "none",
        })}
      >
        <MenubarPrimitive.ItemIndicator>
          <LuCircle className={css({ w: "2", h: "2", fill: "current" })} />
        </MenubarPrimitive.ItemIndicator>
      </span>
      {children}
    </MenubarPrimitive.RadioItem>
  );
}
const MenubarRadioItem = withContext(RadioItem, "radioItem");

function Label({
  inset,
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.Label> & { inset?: boolean }) {
  return <MenubarPrimitive.Label data-slot="menubar-label" data-inset={inset} {...props} />;
}
const MenubarLabel = withContext(Label, "label");

const MenubarSeparator = withContext(MenubarPrimitive.Separator, "separator", {
  defaultProps: {
    "data-slot": "menubar-separator",
  },
});

const MenubarShortcut = withContext("span", "shortcut", {
  defaultProps: {
    "data-slot": "menubar-shortcut",
  },
});

const MenubarSub = MenubarPrimitive.Sub;

function SubTrigger({
  inset,
  children,
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.SubTrigger> & { inset?: boolean }) {
  return (
    <MenubarPrimitive.SubTrigger data-slot="menubar-sub-trigger" data-inset={inset} {...props}>
      {children}
      <LuChevronRight className={css({ ml: "auto", w: "4", h: "4" })} />
    </MenubarPrimitive.SubTrigger>
  );
}
const MenubarSubTrigger = withContext(SubTrigger, "subTrigger");

const MenubarSubContent = withContext(MenubarPrimitive.SubContent, "subContent", {
  defaultProps: {
    "data-slot": "menubar-sub-content",
  },
});

export {
  Menubar,
  MenubarPortal,
  MenubarMenu,
  MenubarTrigger,
  MenubarContent,
  MenubarGroup,
  MenubarSeparator,
  MenubarLabel,
  MenubarItem,
  MenubarShortcut,
  MenubarCheckboxItem,
  MenubarRadioGroup,
  MenubarRadioItem,
  MenubarSub,
  MenubarSubTrigger,
  MenubarSubContent,
};

import { defineSlotRecipe } from "@pandacss/dev";

export const menubarSlotRecipe = defineSlotRecipe({
  className: "menubar",
  slots: [
    "root",
    "menu",
    "group",
    "portal",
    "radioGroup",
    "trigger",
    "content",
    "item",
    "checkboxItem",
    "radioItem",
    "itemIndicator",
    "label",
    "separator",
    "shortcut",
    "sub",
    "subTrigger",
    "subContent",
  ],
  base: {
    root: {
      display: "flex",
      alignItems: "center",
      gap: "1",
      h: "9",
      p: "1",
      bg: "bg",
      borderWidth: "1px",
      rounded: "md",
      shadow: "xs",
    },

    menu: {},

    trigger: {
      display: "flex",
      alignItems: "center",
      px: "2",
      py: "1",
      textStyle: "sm",
      fontWeight: "medium",
      rounded: "sm",
      cursor: "default",
      userSelect: "none",
      outline: "none",
      _focus: {
        bg: "accent",
        color: "accent.fg",
      },
      _open: {
        bg: "accent",
        color: "accent.fg",
      },
    },

    content: {
      zIndex: 50,
      minW: "12rem",
      overflow: "hidden",
      p: "1",
      bg: "popover",
      color: "popover.fg",
      borderWidth: "1px",
      rounded: "md",
      shadow: "md",
      transformOrigin: "var(--radix-menubar-content-transform-origin)",
      _open: {
        animateIn: true,
        fadeIn: "0",
        zoomIn: "95",
      },
      "&[data-side=top]": {
        slideInFromBottom: "2",
      },
      "&[data-side=bottom]": {
        slideInFromTop: "2",
      },
      "&[data-side=left]": {
        slideInFromRight: "2",
      },
      "&[data-side=right]": {
        slideInFromLeft: "2",
      },
    },

    item: {
      position: "relative",
      display: "flex",
      alignItems: "center",
      px: "2",
      py: "1.5",
      textStyle: "sm",
      rounded: "sm",
      outline: "none",
      cursor: "default",
      userSelect: "none",
      _focus: {
        bg: "accent",
        color: "accent.fg",
      },
      _disabled: {
        pointerEvents: "none",
        opacity: "0.5",
      },
      "&[data-inset=true]": {
        pl: "8",
      },
      "&[data-variant=danger]": {
        color: "danger",
        _focus: {
          bg: "danger/10",
          color: "danger",
        },
        _dark: {
          _focus: {
            bg: "danger/20",
          },
        },
        "* svg": {
          color: "danger!",
        },
      },
      "& svg": {
        w: "4",
        h: "4",
        flexShrink: "0",
        color: "muted.fg",
        pointerEvents: "none",
      },
    },

    checkboxItem: {
      pos: "relative",
      display: "flex",
      alignItems: "center",
      gap: "2",
      py: "1.5",
      pl: "8",
      pr: "2",
      textStyle: "sm",
      rounded: "xs",
      outline: "none",
      cursor: "default",
      userSelect: "none",
      _focus: {
        bg: "accent",
        color: "accent.fg",
      },
      _disabled: {
        pointerEvents: "none",
        opacity: "0.5",
      },
      "& svg": {
        w: "4",
        h: "4",
        flexShrink: "0",
        pointerEvents: "none",
      },
    },

    radioItem: {
      pos: "relative",
      display: "flex",
      alignItems: "center",
      gap: "2",
      py: "1.5",
      pl: "8",
      pr: "2",
      textStyle: "sm",
      rounded: "xs",
      outline: "none",
      cursor: "default",
      userSelect: "none",
      _focus: {
        bg: "accent",
        color: "accent.fg",
      },
      _disabled: {
        pointerEvents: "none",
        opacity: "0.5",
      },
      "& svg": {
        w: "4",
        h: "4",
        flexShrink: "0",
        pointerEvents: "none",
      },
    },

    label: {
      px: "2",
      py: "1.5",
      textStyle: "sm",
      fontWeight: "medium",
      "&[data-inset=true]": {
        pl: "8",
      },
    },

    separator: {
      mx: "-1",
      my: "1",
      h: "1px",
      bg: "border",
    },

    shortcut: {
      ml: "auto",
      color: "muted.fg",
      textStyle: "xs",
      letterSpacing: "widest",
    },

    subTrigger: {
      display: "flex",
      alignItems: "center",
      px: "2",
      py: "1.5",
      textStyle: "sm",
      rounded: "sm",
      outline: "none",
      cursor: "default",
      userSelect: "none",
      _focus: {
        bg: "accent",
        color: "accent.fg",
      },
      _open: {
        bg: "accent",
        color: "accent.fg",
      },
      "&[data-inset=true]": {
        pl: "8",
      },
    },

    subContent: {
      zIndex: "50",
      minW: "8rem",
      overflow: "hidden",
      bg: "popover",
      color: "popover.fg",
      p: "1",
      borderWidth: "1px",
      rounded: "md",
      shadow: "lg",
      transformOrigin: "var(--radix-menubar-content-transform-origin)",
      _open: {
        animateIn: true,
        fadeIn: "0",
        zoomIn: "95",
      },
      _closed: {
        animateOut: true,
        fadeOut: "0",
        zoomOut: "95",
      },
      "&[data-side=top]": {
        slideInFromBottom: "2",
      },
      "&[data-side=bottom]": {
        slideInFromTop: "2",
      },
      "&[data-side=left]": {
        slideInFromRight: "2",
      },
      "&[data-side=right]": {
        slideInFromLeft: "2",
      },
    },
  },
});
