"use client";

import { LuChevronDown } from "react-icons/lu";
import { css } from "styled-system/css";
import { createStyleContext, styled } from "styled-system/jsx";
import { nativeSelect } from "styled-system/recipes";

const { withProvider, withContext } = createStyleContext(nativeSelect);

function NativeSelectBase(props: React.ComponentProps<"select">) {
  return (
    <styled.div
      className="group-native-select"
      data-slot="native-select-wrapper"
      css={{ pos: "relative", w: "fit", "&:has(select:disabled)": { opacity: "0.5" } }}
    >
      <select data-slot="native-select" {...props} />
      <LuChevronDown
        className={css({
          color: "muted.fg",
          pointerEvents: "none",
          pos: "absolute",
          top: "50%",
          right: "3.5",
          w: "4",
          h: "4",
          transform: "translateY(-50%)",
          opacity: "0.5",
          userSelect: "none",
        })}
        aria-hidden="true"
        data-slot="native-select-icon"
      />
    </styled.div>
  );
}

const NativeSelect = withProvider(NativeSelectBase, "root");

const NativeSelectOption = withContext("option", "option", {
  defaultProps: {
    "data-slot": "native-select-option",
  },
});

const NativeSelectOptGroup = withContext("optgroup", "optGroup", {
  defaultProps: {
    "data-slot": "native-select-optgroup",
  },
});

export { NativeSelect, NativeSelectOptGroup, NativeSelectOption };

import { defineSlotRecipe } from "@pandacss/dev";

export const nativeSelectSlotRecipe = defineSlotRecipe({
  className: "native-select",
  slots: ["root", "option", "optGroup"],
  base: {
    root: {
      bg: "transparent",
      px: "3",
      py: "2",
      pr: "9",
      w: "full",
      h: "9",
      minW: "0",
      appearance: "none",
      borderWidth: "1px",
      borderColor: "input",
      rounded: "md",
      shadow: "xs",
      textStyle: "sm",
      transition: "common",
      outlineColor: "transparent",
      _placeholder: {
        color: "muted.fg",
      },
      _selection: {
        bg: "primary",
        color: "primary.fg",
      },
      _disabled: {
        pointerEvents: "none",
        cursor: "not-allowed",
      },
      _focusVisible: {
        borderColor: "ring",
        outlineWidth: "3px",
        outlineStyle: "solid",
        outlineColor: "ring/50",
      },
      "&[aria-invalid=true]": {
        borderColor: "danger!",
        outlineColor: "danger/20!",
        _dark: {
          outlineColor: "danger/40!",
        },
      },
      _dark: {
        bg: "input/30",
        _hover: {
          bg: "input/50",
        },
      },
    },
  },
});
