"use client";

import * as React from "react";
import * as ProgressPrimitive from "@radix-ui/react-progress";
import { cx } from "styled-system/css";
import { styled } from "styled-system/jsx";
import { progress } from "styled-system/recipes";

const classes = progress();

function Root({ className, value, ...props }: React.ComponentProps<typeof ProgressPrimitive.Root>) {
  return (
    <ProgressPrimitive.Root className={cx(classes.root, className)} {...props}>
      <ProgressPrimitive.Indicator
        className={classes.indicator}
        style={{ transform: `translateX(-${100 - (value || 0)}%)` }}
      />
    </ProgressPrimitive.Root>
  );
}
const Progress = styled(Root);
Progress.displayName = ProgressPrimitive.Root.displayName;

export { Progress };

import { defineSlotRecipe } from "@pandacss/dev";

export const progressSlotRecipe = defineSlotRecipe({
  className: "progress",
  slots: ["root", "indicator"],
  base: {
    root: {
      pos: "relative",
      w: "full",
      h: "4",
      overflow: "hidden",
      rounded: "full",
      bg: "primary/20",
    },

    indicator: {
      w: "full",
      h: "full",
      flex: "1",
      bg: "primary",
      transition: "all",
    },
  },
});
