"use client";

import * as React from "react";
import { LuCircle } from "react-icons/lu";
import * as RadioGroupPrimitive from "@radix-ui/react-radio-group";
import { css } from "styled-system/css";
import { createStyleContext } from "styled-system/jsx";
import { radioGroup } from "styled-system/recipes";

const { withProvider, withContext } = createStyleContext(radioGroup);

const RadioGroup = withProvider(RadioGroupPrimitive.Root, "root");
const RadioGroupIndicator = withContext(RadioGroupPrimitive.Indicator, "indicator");

function Item(props: React.ComponentProps<typeof RadioGroupPrimitive.Item>) {
  return (
    <RadioGroupPrimitive.Item {...props}>
      <RadioGroupIndicator>
        <LuCircle
          className={css({
            pos: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            w: "2",
            h: "2",
            fill: "primary",
          })}
        />
      </RadioGroupIndicator>
    </RadioGroupPrimitive.Item>
  );
}
const RadioGroupItem = withContext(Item, "item");

export { RadioGroup, RadioGroupItem };

import { defineSlotRecipe } from "@pandacss/dev";

export const radioGroupSlotRecipe = defineSlotRecipe({
  className: "radio-group",
  slots: ["root", "item", "indicator"],
  base: {
    root: {
      display: "grid",
      gap: "3",
    },

    item: {
      aspectRatio: "square",
      w: "4",
      h: "4",
      flexShrink: "0",
      color: "primary",
      borderWidth: "1px",
      borderColor: "input",
      rounded: "full",
      shadow: "xs",
      transition: "common",
      outlineColor: "transparent",
      _focusVisible: {
        borderColor: "ring",
        outlineWidth: "3px",
        outlineStyle: "solid",
        outlineColor: "ring/50",
      },
      _disabled: {
        cursor: "not-allowed",
        opacity: "0.5",
      },
      "&[aria-invalid=true]": {
        borderColor: "danger!",
        outlineColor: "danger/20!",
        _dark: {
          outlineColor: "danger/40!",
        },
      },
      _dark: {
        bg: "input/30",
      },
    },

    indicator: {
      pos: "relative",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
    },
  },
});
