"use client";

import * as React from "react";
import * as ScrollAreaPrimitive from "@radix-ui/react-scroll-area";
import { cx } from "styled-system/css";
import { styled } from "styled-system/jsx";
import { scrollArea } from "styled-system/recipes";

const classes = scrollArea();

function Root({
  className,
  children,
  ...props
}: React.ComponentProps<typeof ScrollAreaPrimitive.Root>) {
  return (
    <ScrollAreaPrimitive.Root
      data-slot="scroll-area"
      className={cx(classes.root, className)}
      {...props}
    >
      <ScrollAreaPrimitive.Viewport data-slot="scroll-area-viewport" className={classes.viewport}>
        {children}
      </ScrollAreaPrimitive.Viewport>
      <Scrollbar />
      <ScrollAreaPrimitive.Corner />
    </ScrollAreaPrimitive.Root>
  );
}
const ScrollArea = styled(Root);
ScrollArea.displayName = ScrollAreaPrimitive.Root.displayName;

function ScrollbarBase({
  className,
  orientation = "vertical",
  ...props
}: React.ComponentProps<typeof ScrollAreaPrimitive.Scrollbar>) {
  return (
    <ScrollAreaPrimitive.Scrollbar
      className={cx(classes.scrollAreaScrollbar, className)}
      orientation={orientation}
      data-orientation={orientation}
      data-slot="scroll-area-scrollbar"
      {...props}
    >
      <ScrollAreaPrimitive.Thumb
        data-slot="scroll-area-thumb"
        className={classes.scrollAreaThumb}
      />
    </ScrollAreaPrimitive.Scrollbar>
  );
}
const Scrollbar = styled(ScrollbarBase);
Scrollbar.displayName = ScrollAreaPrimitive.Scrollbar.displayName;

export { ScrollArea, Scrollbar };

import { defineSlotRecipe } from "@pandacss/dev";

export const scrollAreaSlotRecipe = defineSlotRecipe({
  className: "scroll-area",
  slots: ["root", "viewport", "scrollAreaScrollbar", "scrollAreaThumb"],
  base: {
    root: {
      pos: "relative",
    },

    viewport: {
      w: "full",
      h: "full",
      rounded: "inherit",
      transition: "common",
      outlineColor: "transparent",
      _focusVisible: {
        outlineWidth: "3px",
        outlineStyle: "solid",
        outlineColor: "ring/50",
      },
    },

    scrollAreaScrollbar: {
      display: "flex",
      p: "1px",
      touchAction: "none",
      userSelect: "none",
      transition: "colors",
      _horizontal: {
        h: "2.5",
        flexDir: "column",
        borderTopWidth: "1px",
        borderTopColor: "transparent",
      },
      _vertical: {
        w: "2.5",
        h: "full",
        borderLeftWidth: "1px",
        borderLeftColor: "transparent",
      },
    },

    scrollAreaThumb: {
      pos: "relative",
      flex: "1",
      bg: "border",
      rounded: "full",
    },
  },
});
