export function AppSidebar({ ...props }: React.ComponentProps<typeof Sidebar>) {
  return (
    <Sidebar {...props}>
      <SidebarHeader>
        <SidebarMenu>
          <SidebarMenuItem>
            <SidebarMenuButton size="lg" asChild>
              <a href="#">
                <styled.div
                  css={{
                    bg: "sidebar.primary",
                    color: "sidebar.primary.fg",
                    display: "flex",
                    aspectRatio: "square",
                    w: "8",
                    h: "8",
                    alignItems: "center",
                    justifyContent: "center",
                    rounded: "lg",
                  }}
                >
                  <LuGalleryVerticalEnd className={css({ w: "4", h: "4" })} />
                </styled.div>
                <styled.div
                  css={{ display: "flex", flexDir: "column", gap: "0.5", lineHeight: "none" }}
                >
                  <styled.span css={{ fontWeight: "medium" }}>Documentation</styled.span>
                  <span>v1.0.0</span>
                </styled.div>
              </a>
            </SidebarMenuButton>
          </SidebarMenuItem>
        </SidebarMenu>
        <SearchForm />
      </SidebarHeader>
      <SidebarContent>
        <SidebarGroup>
          <SidebarMenu>
            {data.navMain.map((item, index) => (
              <Collapsible key={item.title} defaultOpen={index === 1} className="collapsible">
                <SidebarMenuItem>
                  <CollapsibleTrigger asChild>
                    <SidebarMenuButton>
                      {item.title}{" "}
                      <LuPlus
                        className={css({
                          ml: "auto",
                          ".collapsible[data-state=open] &": { display: "none" },
                        })}
                      />
                      <LuMinus
                        className={css({
                          ml: "auto",
                          ".collapsible[data-state=closed] &": { display: "none" },
                        })}
                      />
                    </SidebarMenuButton>
                  </CollapsibleTrigger>
                  {item.items?.length ? (
                    <CollapsibleContent>
                      <SidebarMenuSub>
                        {item.items.map((item) => (
                          <SidebarMenuSubItem key={item.title}>
                            <SidebarMenuSubButton asChild isActive={item.isActive}>
                              <a href={item.url}>{item.title}</a>
                            </SidebarMenuSubButton>
                          </SidebarMenuSubItem>
                        ))}
                      </SidebarMenuSub>
                    </CollapsibleContent>
                  ) : null}
                </SidebarMenuItem>
              </Collapsible>
            ))}
          </SidebarMenu>
        </SidebarGroup>
      </SidebarContent>
      <SidebarRail />
    </Sidebar>
  );
}

import { LuSearch } from "react-icons/lu";
import { css } from "styled-system/css";
import { Label } from "@/registry/default/ui/label";
import { SidebarGroup, SidebarGroupContent, SidebarInput } from "@/registry/default/ui/sidebar";

export function SearchForm({ ...props }: React.ComponentProps<"form">) {
  return (
    <form {...props}>
      <SidebarGroup css={{ py: "0" }}>
        <SidebarGroupContent css={{ pos: "relative" }}>
          <Label htmlFor="search" css={{ srOnly: true }}>
            Search
          </Label>
          <SidebarInput id="search" placeholder="Search the docs..." css={{ pl: "8" }} />
          <LuSearch
            className={css({
              pointerEvents: "none",
              pos: "absolute",
              top: "50%",
              left: "2",
              w: "4",
              h: "4",
              transform: "translateY(-50%)",
              opacity: "0.5",
              userSelect: "none",
            })}
          />
        </SidebarGroupContent>
      </SidebarGroup>
    </form>
  );
}
