    return null;
  }

  return (
    <SidebarMenu>
      <SidebarMenuItem>
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <SidebarMenuButton css={{ w: "fit", px: "1.5" }}>
              <styled.div
                css={{
                  bg: "sidebar.primary",
                  color: "sidebar.primary.fg",
                  display: "flex",
                  aspectRatio: "square",
                  w: "5",
                  h: "5",
                  alignItems: "center",
                  justifyContent: "center",
                  rounded: "md",
                }}
              >
                <activeTeam.logo className={css({ w: "3", h: "3" })} />
              </styled.div>
              <styled.span css={{ truncate: true, fontWeight: "medium" }}>
                {activeTeam.name}
              </styled.span>
              <LuChevronDown className={css({ opacity: "0.5" })} />
            </SidebarMenuButton>
          </DropdownMenuTrigger>
          <DropdownMenuContent
            css={{ w: "64", rounded: "lg" }}
            align="start"
            side="bottom"
            sideOffset={4}
          >
            <DropdownMenuLabel css={{ color: "muted.fg", textStyle: "xs" }}>
              Teams
            </DropdownMenuLabel>
            {teams.map((team, index) => (
              <DropdownMenuItem
                key={team.name}
                onClick={() => setActiveTeam(team)}
                css={{ gap: "2", p: "2" }}
              >
                <styled.div
                  css={{
                    display: "flex",
                    w: "6",
                    h: "6",
                    alignItems: "center",
                    justifyContent: "center",
                    rounded: "xs",
                    borderWidth: "1px",
