import { styled } from "styled-system/jsx";
import { AppSidebar } from "@/registry/default/blocks/sidebar-12/components/app-sidebar";
import {
  Breadcrumb,
  BreadcrumbItem,
  BreadcrumbList,
  BreadcrumbPage,
} from "@/registry/default/ui/breadcrumb";
import { Separator } from "@/registry/default/ui/separator";
import { SidebarInset, SidebarProvider, SidebarTrigger } from "@/registry/default/ui/sidebar";

export default function Page() {
  return (
    <SidebarProvider>
      <AppSidebar />
      <SidebarInset>
        <styled.header
          css={{
            bg: "bg",
            pos: "sticky",
            top: "0",
            display: "flex",
            h: "16",
            flexShrink: "0",
            alignItems: "center",
            gap: "2",
            borderBottomWidth: "1px",
            px: "4",
          }}
        >
          <SidebarTrigger css={{ ml: "-1" }} />
          <Separator orientation="vertical" css={{ mr: "2", _vertical: { h: "4" } }} />
          <Breadcrumb>
            <BreadcrumbList>
              <BreadcrumbItem>
                <BreadcrumbPage>October 2024</BreadcrumbPage>
              </BreadcrumbItem>
            </BreadcrumbList>
          </Breadcrumb>
        </styled.header>
        <styled.div css={{ display: "flex", flex: "1", flexDir: "column", gap: "4", p: "4" }}>
          <styled.div
            css={{
              display: "grid",
              gridAutoRows: "min",
              gap: "4",
              md: { gridTemplateColumns: "repeat(5, minmax(0, 1fr))" },
            }}
          >
            {Array.from({ length: 20 }).map((_, i) => (
              <styled.div key={i} css={{ bg: "muted/50", aspectRatio: "square", rounded: "xl" }} />
            ))}
          </styled.div>
        </styled.div>
      </SidebarInset>
    </SidebarProvider>
  );
}

import * as React from "react";
import { LuPlus } from "react-icons/lu";
import { Calendars } from "@/registry/default/blocks/sidebar-12/components/calendars";
import { DatePicker } from "@/registry/default/blocks/sidebar-12/components/date-picker";
import { NavUser } from "@/registry/default/blocks/sidebar-12/components/nav-user";
import {
  Sidebar,
  SidebarContent,
  SidebarFooter,
  SidebarHeader,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarRail,
  SidebarSeparator,
} from "@/registry/default/ui/sidebar";

// This is sample data.
const data = {
  user: {
    name: "shadcn",
    email: "m@example.com",
    avatar: "https://github.com/shadcn.png",
  },
  calendars: [
    {
      name: "My Calendars",
      items: ["Personal", "Work", "Family"],
    },
    {
      name: "Favorites",
      items: ["Holidays", "Birthdays"],
    },
    {
      name: "Other",
      items: ["Travel", "Reminders", "Deadlines"],
    },
  ],
};

export function AppSidebar({ ...props }: React.ComponentProps<typeof Sidebar>) {
  return (
    <Sidebar {...props}>
      <SidebarHeader css={{ borderColor: "sidebar.border", h: "16", borderBottomWidth: "1px" }}>
        <NavUser user={data.user} />
      </SidebarHeader>
      <SidebarContent>
        <DatePicker />
        <SidebarSeparator css={{ mx: "0" }} />
        <Calendars calendars={data.calendars} />
      </SidebarContent>
      <SidebarFooter>
        <SidebarMenu>
          <SidebarMenuItem>
            <SidebarMenuButton>
              <LuPlus />
              <span>New Calendar</span>
            </SidebarMenuButton>
          </SidebarMenuItem>
        </SidebarMenu>
      </SidebarFooter>
      <SidebarRail />
    </Sidebar>
  );
}

import * as React from "react";
import { LuCheck, LuChevronRight } from "react-icons/lu";
import { css } from "styled-system/css";
import { styled } from "styled-system/jsx";
import {
  Collapsible,
  CollapsibleContent,
  CollapsibleTrigger,
} from "@/registry/default/ui/collapsible";
import {
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarSeparator,
} from "@/registry/default/ui/sidebar";

export function Calendars({
  calendars,
}: {
  calendars: {
    name: string;
    items: string[];
  }[];
}) {
  return (
    <>
      {calendars.map((calendar, index) => (
        <React.Fragment key={calendar.name}>
          <SidebarGroup key={calendar.name} className="py-0">
            <Collapsible defaultOpen={index === 0} className="group-collapsible">
              <SidebarGroupLabel
                asChild
                css={{
                  w: "full",
                  color: "sidebar.fg",
                  textStyle: "sm",
                  _hover: {
                    bg: "sidebar.accent",
                    color: "sidebar.accent.fg",
                  },
                }}
              >
                <CollapsibleTrigger>
                  {calendar.name}{" "}
                  <LuChevronRight
                    className={css({
                      ml: "auto",
                      transition: "transform",
                      ".group-collapsible[data-state=open] &": { transform: "rotate(90deg)" },
                    })}
                  />
                </CollapsibleTrigger>
              </SidebarGroupLabel>
              <CollapsibleContent>
                <SidebarGroupContent>
                  <SidebarMenu>
                    {calendar.items.map((item, index) => (
                      <SidebarMenuItem key={item}>
                        <SidebarMenuButton>
                          <styled.div
                            data-active={index < 2}
                            css={{
                              display: "flex",
                              aspectRatio: "square",
                              w: "4",
                              h: "4",
                              flexShrink: "0",
                              alignItems: "center",
                              justifyContent: "center",
                              color: "sidebar.primary.fg",
                              borderWidth: "1px",
                              borderColor: "sidebar.border",
                              rounded: "sm",
                              "&[data-active=true]": {
                                bg: "sidebar.primary",
                                borderColor: "sidebar.primary",
                              },
                            }}
                            className="group-calendar-item"
                          >
                            <LuCheck
                              className={css({
                                display: "none",
                                w: "3",
                                h: "3",
                                ".group-calendar-item[data-active=true] &": {
                                  display: "block",
                                },
                              })}
                            />
                          </styled.div>
                          {item}
                        </SidebarMenuButton>
                      </SidebarMenuItem>
                    ))}
                  </SidebarMenu>
                </SidebarGroupContent>
              </CollapsibleContent>
            </Collapsible>
          </SidebarGroup>
          <SidebarSeparator css={{ mx: "0" }} />
        </React.Fragment>
      ))}
    </>
  );
}

import { Calendar } from "@/registry/default/ui/calendar";
import { SidebarGroup, SidebarGroupContent } from "@/registry/default/ui/sidebar";

export function DatePicker() {
  return (
    <SidebarGroup css={{ px: "0" }}>
      <SidebarGroupContent>
        <Calendar
          css={{
            "& [role=gridcell]": {
              w: "33px",
            },
          }}
        />
      </SidebarGroupContent>
    </SidebarGroup>
  );
}

"use client";

import {
  LuBadgeCheck,
  LuBell,
  LuChevronsUpDown,
  LuCreditCard,
  LuLogOut,
  LuSparkles,
} from "react-icons/lu";
import { css } from "styled-system/css";
import { styled } from "styled-system/jsx";
import { Avatar, AvatarFallback, AvatarImage } from "@/registry/default/ui/avatar";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuGroup,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/registry/default/ui/dropdown-menu";
import {
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  useSidebar,
} from "@/registry/default/ui/sidebar";

export function NavUser({
  user,
}: {
  user: {
    name: string;
    email: string;
    avatar: string;
  };
}) {
  const { isMobile } = useSidebar();

  return (
    <SidebarMenu>
      <SidebarMenuItem>
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <SidebarMenuButton
              size="lg"
              css={{
                "&[data-state=open]": {
                  bg: "sidebar.accent",
                  color: "sidebar.accent.fg",
                },
              }}
            >
              <Avatar css={{ w: "8", h: "8", rounded: "lg" }}>
                <AvatarImage src={user.avatar} alt={user.name} />
                <AvatarFallback css={{ rounded: "lg" }}>CN</AvatarFallback>
              </Avatar>
              <styled.div
                css={{
                  display: "grid",
                  flex: "1",
                  textAlign: "left",
                  textStyle: "sm",
                  lineHeight: "tight",
                }}
              >
                <styled.span css={{ truncate: true, fontWeight: "medium" }}>
                  {user.name}
                </styled.span>
                <styled.span css={{ truncate: true, textStyle: "xs" }}>{user.email}</styled.span>
              </styled.div>
              <LuChevronsUpDown className={css({ ml: "auto", w: "4", h: "4" })} />
            </SidebarMenuButton>
          </DropdownMenuTrigger>
          <DropdownMenuContent
            css={{ w: "var(--radix-dropdown-menu-trigger-width)", minW: "56", rounded: "lg" }}
            side={isMobile ? "bottom" : "right"}
            align="start"
            sideOffset={4}
          >
            <DropdownMenuLabel css={{ p: "0", fontWeight: "normal" }}>
              <styled.div
                css={{
                  display: "flex",
                  alignItems: "center",
                  gap: "2",
                  px: "1",
                  py: "1.5",
                  textAlign: "left",
                  textStyle: "sm",
                }}
              >
                <Avatar css={{ w: "8", h: "8", rounded: "lg" }}>
                  <AvatarImage src={user.avatar} alt={user.name} />
                  <AvatarFallback css={{ rounded: "lg" }}>CN</AvatarFallback>
                </Avatar>
                <styled.div
                  css={{
                    display: "grid",
                    flex: "1",
                    textAlign: "left",
                    textStyle: "sm",
                    lineHeight: "tight",
                  }}
                >
                  <styled.span css={{ truncate: true, fontWeight: "medium" }}>
                    {user.name}
                  </styled.span>
                  <styled.span css={{ truncate: true, textStyle: "xs" }}>{user.email}</styled.span>
                </styled.div>
              </styled.div>
            </DropdownMenuLabel>
            <DropdownMenuSeparator />
            <DropdownMenuGroup>
              <DropdownMenuItem>
                <LuSparkles />
                Upgrade to Pro
              </DropdownMenuItem>
            </DropdownMenuGroup>
            <DropdownMenuSeparator />
            <DropdownMenuGroup>
              <DropdownMenuItem>
                <LuBadgeCheck />
                Account
              </DropdownMenuItem>
              <DropdownMenuItem>
                <LuCreditCard />
                Billing
              </DropdownMenuItem>
              <DropdownMenuItem>
                <LuBell />
                Notifications
              </DropdownMenuItem>
            </DropdownMenuGroup>
            <DropdownMenuSeparator />
            <DropdownMenuItem>
              <LuLogOut />
              Log out
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      </SidebarMenuItem>
    </SidebarMenu>
  );
}
