export default function Page() {
  return (
    <SidebarProvider>
      <SidebarLeft />
      <SidebarInset>
        <styled.header
          css={{
            bg: "bg",
            pos: "sticky",
            top: "0",
            display: "flex",
            h: "14",
            flexShrink: "0",
            alignItems: "center",
            gap: "2",
          }}
        >
          <styled.div css={{ display: "flex", flex: "1", alignItems: "center", gap: "2", px: "3" }}>
            <SidebarTrigger />
            <Separator orientation="vertical" css={{ mr: "2", _vertical: { h: "4" } }} />
            <Breadcrumb>
              <BreadcrumbList>
                <BreadcrumbItem>
                  <BreadcrumbPage css={{ lineClamp: "1" }}>
                    Project Management & Task Tracking
                  </BreadcrumbPage>
                </BreadcrumbItem>
              </BreadcrumbList>
            </Breadcrumb>
          </styled.div>
        </styled.header>
        <styled.div css={{ display: "flex", flex: "1", flexDir: "column", gap: "4", p: "4" }}>
          <styled.div
            css={{ bg: "muted/50", mx: "auto", h: "24", w: "full", maxW: "3xl", rounded: "xl" }}
          />
          <styled.div
            css={{ bg: "muted/50", mx: "auto", h: "100vh", w: "full", maxW: "3xl", rounded: "xl" }}
          />
        </styled.div>
      </SidebarInset>
      <SidebarRight />
    </SidebarProvider>
  );
}

import * as React from "react";
import { LuCheck, LuChevronRight } from "react-icons/lu";
import { css } from "styled-system/css";
import { styled } from "styled-system/jsx";
import {
  Collapsible,
  CollapsibleContent,
  CollapsibleTrigger,
} from "@/registry/default/ui/collapsible";
import {
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarSeparator,
} from "@/registry/default/ui/sidebar";

export function Calendars({
  calendars,
}: {
  calendars: {
    name: string;
    items: string[];
  }[];
}) {
  return (
    <>
      {calendars.map((calendar, index) => (
        <React.Fragment key={calendar.name}>
          <SidebarGroup key={calendar.name} css={{ py: "0" }}>
            <Collapsible defaultOpen={index === 0} className="group-collapsible">
              <SidebarGroupLabel
                asChild
                className="group-label"
                css={{
                  w: "full",
                  color: "sidebar.fg",
                  textStyle: "sm",
                  _hover: {
                    bg: "sidebar.accent",
                    color: "sidebar.accent.fg",
                  },
                }}
              >
                <CollapsibleTrigger>
                  {calendar.name}{" "}
                  <LuChevronRight
                    className={css({
                      ml: "auto",
                      transition: "transform",
                      ".group-collapsible[data-state=open] &": { transform: "rotate(90deg)" },
                    })}
                  />
                </CollapsibleTrigger>
              </SidebarGroupLabel>
              <CollapsibleContent>
                <SidebarGroupContent>
                  <SidebarMenu>
                    {calendar.items.map((item, index) => (
                      <SidebarMenuItem key={item}>
                        <SidebarMenuButton>
                          <styled.div
                            data-active={index < 2}
                            className="group-calendar-item"
                            css={{
                              display: "flex",
                              aspectRatio: "square",
                              w: "4",
                              h: "4",
                              flexShrink: "0",
                              alignItems: "center",
                              justifyContent: "center",
                              rounded: "xs",
                              borderWidth: "1px",
                              borderColor: "sidebar.border",
                              color: "sidebar.primary.fg",
                              "&[data-active=true]": {
                                bg: "sidebar.primary",
                                borderColor: "sidebar.primary",
                              },
                            }}
                          >
                            <LuCheck
                              className={css({
                                display: "none",
                                w: "3",
                                h: "3",
                                ".group-calendar-item[data-active=true] &": { display: "block" },
                              })}
                            />
                          </styled.div>
                          {item}
                        </SidebarMenuButton>
                      </SidebarMenuItem>
                    ))}
                  </SidebarMenu>
                </SidebarGroupContent>
              </CollapsibleContent>
            </Collapsible>
          </SidebarGroup>
          <SidebarSeparator css={{ mx: "0" }} />
        </React.Fragment>
      ))}
    </>
  );
}

