"use client";

import * as React from "react";
import { css } from "styled-system/css";
import { createStyleContext } from "styled-system/jsx";
import { table } from "styled-system/recipes";

const { withProvider, withContext } = createStyleContext(table);

function Root(props: React.ComponentProps<"table">) {
  return (
    <div
      data-slot="table-container"
      className={css({ pos: "relative", w: "full", overflowX: "auto" })}
    >
      <table data-slot="table" {...props} />
    </div>
  );
}

const Table = withProvider(Root, "root", {
  defaultProps: {
    role: "table",
  },
});

const TableHeader = withContext("thead", "header", {
  defaultProps: {
    "data-slot": "table-header",
  },
});

const TableBody = withContext("tbody", "body", {
  defaultProps: {
    "data-slot": "table-body",
  },
});

const TableFooter = withContext("tfoot", "footer", {
  defaultProps: {
    "data-slot": "table-footer",
  },
});

const TableRow = withContext("tr", "row", {
  defaultProps: {
    "data-slot": "table-row",
  },
});

const TableHead = withContext("th", "head", {
  defaultProps: {
    "data-slot": "table-head",
  },
});

const TableCell = withContext("td", "cell", {
  defaultProps: {
    "data-slot": "table-cell",
  },
});

const TableCaption = withContext("caption", "caption", {
  defaultProps: {
    "data-slot": "table-caption",
  },
});

export { Table, TableHeader, TableBody, TableFooter, TableRow, TableHead, TableCell, TableCaption };

import { defineSlotRecipe } from "@pandacss/dev";

export const tableSlotRecipe = defineSlotRecipe({
  className: "table",
  slots: ["root", "header", "body", "footer", "row", "head", "cell", "caption"],
  base: {
    root: {
      w: "full",
      captionSide: "bottom",
      textStyle: "sm",
    },

    header: {
      "& tr": {
        borderBottomWidth: "1px",
      },
    },

    body: {
      "& tr:last-child": {
        borderWidth: "0",
      },
    },

    footer: {
      borderTop: "1px",
      bg: "muted/50",
      fontWeight: "medium",
      "& > tr:last-child": {
        borderBottomWidth: "0",
      },
    },

    row: {
      borderBottomWidth: "1px",
      transition: "colors",
      _hover: {
        bg: "muted/50",
      },
      _selected: {
        bg: "muted",
      },
    },

    head: {
      h: "10",
      px: "2",
      textAlign: "left",
      verticalAlign: "middle",
      fontWeight: "medium",
      color: "fg",
      whiteSpace: "nowrap",
      "&:has([role=checkbox])": {
        pr: "0",
      },
      "& > [role=checkbox]": {
        transform: "translateY(-2px)",
      },
    },

    cell: {
      p: "2",
      verticalAlign: "middle",
      whiteSpace: "nowrap",
      "&:has([role=checkbox])": {
        pr: "0",
      },
      "& > [role=checkbox]": {
        transform: "translateY(-2px)",
      },
    },

    caption: {
      mt: "4",
      color: "muted.fg",
      textStyle: "sm",
    },
  },
});
