"use client";

import * as React from "react";
import * as ToggleGroupPrimitive from "@radix-ui/react-toggle-group";
import { cx } from "styled-system/css";
import { styled } from "styled-system/jsx";
import { toggle, toggleGroup, ToggleVariantProps } from "styled-system/recipes";

const classes = toggleGroup();

const ToggleGroupContext = React.createContext<ToggleVariantProps>({
  variant: "plain",
  size: "md",
});

function Root({
  className,
  variant,
  size,
  children,
  ...props
}: React.ComponentProps<typeof ToggleGroupPrimitive.Root> & ToggleVariantProps) {
  return (
    <ToggleGroupPrimitive.Root
      data-variant={variant}
      data-size={size}
      className={cx(classes.root, className)}
      {...props}
    >
      <ToggleGroupContext.Provider value={{ variant, size }}>
        {children}
      </ToggleGroupContext.Provider>
    </ToggleGroupPrimitive.Root>
  );
}
const ToggleGroup = styled(Root);
ToggleGroup.displayName = ToggleGroupPrimitive.Root.displayName;

function Item({
  className,
  children,
  variant,
  size,
  ...props
}: React.ComponentProps<typeof ToggleGroupPrimitive.Item> & ToggleVariantProps) {
  const context = React.useContext(ToggleGroupContext);

  return (
    <ToggleGroupPrimitive.Item
      data-variant={context.variant || variant}
      data-size={context.size || size}
      className={cx(
        toggle({
          variant: context.variant || variant,
          size: context.size || size,
        }),
        classes.item,
        className
      )}
      {...props}
    >
      {children}
    </ToggleGroupPrimitive.Item>
  );
}
const ToggleGroupItem = styled(Item);
ToggleGroupItem.displayName = ToggleGroupPrimitive.Item.displayName;

export { ToggleGroup, ToggleGroupItem };

import { defineSlotRecipe } from "@pandacss/dev";

export const toggleGroupRecipe = defineSlotRecipe({
  className: "toggle-group",
  slots: ["root", "item"],
  base: {
    root: {
      display: "flex",
      alignItems: "center",
      flexWrap: "wrap",
      w: "fit",
      rounded: "md",
      "&[data-variant=outline]": {
        shadow: "xs",
      },
    },

    item: {
      minW: "0",
      flex: "1",
      flexShrink: "0",
      rounded: "0",
      shadow: "none",
      _first: {
        roundedLeft: "md",
      },
      _last: {
        roundedRight: "md",
      },
      _focus: {
        zIndex: "10",
      },
      _focusVisible: {
        zIndex: "10",
      },
      "&[data-variant=outline]": {
        borderLeftWidth: "0!",
        _first: {
          borderLeftWidth: "1px!",
        },
      },
    },
  },
});
